```{.r .cell-code}
model_string <- textConnection("model{
    # Likelihood
    for (i in 1:N_obs) {
        Y[i] ~ dnegbin(q[i], m)
        q[i] <- m / (m + N[i] * lambda[i])
        log(lambda[i]) <- alpha + inprod(X[i, ], beta[])
    }
    for (j in 1:6) {
        beta[j] ~ dnorm(0, 0.0001)
    }

    alpha ~ dnorm(0, 0.0001)
    m ~ dgamma(0.1, 0.1)
}")

data = list(
    X = my_X,
    N = N,
    Y = Y,
    N_obs = length(Y)
